    private void InicioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_InicioActionPerformed
        GUI.txtR.setText("Tiempos de llegada:");
        Carrera.iniciar();
        Inicio.setEnabled(false);
        NuevoCarrera.setEnabled(true);
    }//GEN-LAST:event_InicioActionPerformed

    private void NuevoCarreraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_NuevoCarreraActionPerformed
        Carrera.reiniciar();
        Carro1.setLocation(Carrera.Carros[0].getCoorX(),Carrera.Carros[0].getCoorY());
        Carro2.setLocation(Carrera.Carros[1].getCoorX(),Carrera.Carros[1].getCoorY());
        Carro3.setLocation(Carrera.Carros[2].getCoorX(),Carrera.Carros[2].getCoorY());
        Carro4.setLocation(Carrera.Carros[3].getCoorX(),Carrera.Carros[3].getCoorY());
        Carro5.setLocation(Carrera.Carros[4].getCoorX(),Carrera.Carros[4].getCoorY());
        Carro6.setLocation(Carrera.Carros[5].getCoorX(),Carrera.Carros[5].getCoorY());
        Carro7.setLocation(Carrera.Carros[6].getCoorX(),Carrera.Carros[6].getCoorY());
        Carro8.setLocation(Carrera.Carros[7].getCoorX(),Carrera.Carros[7].getCoorY());
        Carro9.setLocation(Carrera.Carros[8].getCoorX(),Carrera.Carros[8].getCoorY());
        Carro10.setLocation(Carrera.Carros[9].getCoorX(),Carrera.Carros[9].getCoorY());
        Inicio.setEnabled(true);
        NuevoCarrera.setEnabled(false);
    }//GEN-LAST:event_NuevoCarreraActionPerformed

